function deleteThis(id,type,redirect){var you_sure=confirm("Delete this ?");if(you_sure===true){$.post("app/delete.php",{id:id,type:type}).success(function(){window.location=redirect})}else{return false}}function addDateOnCursor(){var todayDate=new Date;var year=todayDate.getFullYear();var month=todayDate.getMonth()+1;if(month<10){month="0"+month}var day=todayDate.getDate();if(day<10){day="0"+day}tinyMCE.activeEditor.execCommand("mceInsertContent",false,year+"-"+month+"-"+day+" ")}function showSaved(){var text="<center><p>Saved !</p></center>";var overlay=document.createElement("div");overlay.setAttribute("id","overlay");overlay.setAttribute("class","overlay");document.body.appendChild(overlay);document.getElementById("overlay").innerHTML=text;window.setTimeout(removeSaved,2e3)}function removeSaved(){$("#overlay").fadeOut(500,function(){$(this).remove()})}function getGetParameters(){var prmstr=window.location.search.substr(1);return prmstr!=null&&prmstr!=""?transformToAssocArray(prmstr):{}}function transformToAssocArray(prmstr){var params={};var prmarr=prmstr.split("&");for(var i=0;i<prmarr.length;i++){var tmparr=prmarr[i].split("=");params[tmparr[0]]=tmparr[1]}return params}function isInt(n){return n%1===0}function showPanel(){var panel=$("#slide-panel");if(panel.css("display")=="none"){panel.css("display","inline")}else{panel.css("display","none")}return false}function showMol(molFileContent){var viewer=new ChemDoodle.ViewerCanvas(Math.random(),100,100);viewer.specs.bonds_width_2D=.6;viewer.specs.bonds_saturationWidth_2D=.18;viewer.specs.bonds_hashSpacing_2D=2.5;viewer.specs.atoms_font_size_2D=10;viewer.specs.atoms_font_families_2D=["Helvetica","Arial","sans-serif"];viewer.specs.atoms_displayTerminalCarbonLabels_2D=true;var mol=ChemDoodle.readMOL(molFileContent);viewer.loadMolecule(mol)}function go_url(x){if(x==""){return}window.location=x}
